import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`;

const Tag = styled.span`
  background-color: #f1f3f4;
  color: #333;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    background-color: #e0e0e0;
  }
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState([]); // Estado para armazenar as tags

  // Carregar dados iniciais ao montar o componente
  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS(); // Busca os dados da API
        console.log('Dados carregados da API:', resultados);

        // Processa as palavras-chave para exibir como tags
        const odsTags = [];
        resultados.forEach((item) => {
          if (item.palavras_chave) {
            odsTags.push(...item.palavras_chave); // Adiciona palavras-chave
          } else if (item.titulo) {
            odsTags.push(item.titulo); // Adiciona o título como fallback
          }
        });

        setTags([...new Set(odsTags)].slice(0, 5)); // Remove duplicatas e limita a 5 tags
      } catch (err) {
        console.error('Erro ao carregar dados iniciais:', err);
        setError('Erro ao carregar dados iniciais.');
      }
    };

    carregarODS();
  }, []);

  // Função de busca
  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = {};

      if (query) {
        params.palavra_resumo = query; // Usa o input como filtro
      }

      const resultados = await buscarTrabalhos(params);
      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  // Função para definir o termo da tag clicada no input
  const handleTagClick = (tag) => {
    setQuery(tag); // Atualiza o input com a tag clicada
    handleSearch(); // Realiza a busca automaticamente
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Exibe as tags carregadas da API */}
      <TagsWrapper>
        {tags.length > 0 ? (
          tags.map((tag, index) => (
            <Tag key={index} onClick={() => handleTagClick(tag)}>
              {tag}
            </Tag>
          ))
        ) : (
          <p>Carregando tags...</p>
        )}
      </TagsWrapper>
    </SearchWrapper>
  );
}

// No final do arquivo SearchBar.js
const handleTagClick = (tag) => {
  setQuery(tag); // Atualiza o campo de busca
  handleSearch(tag); // Realiza a busca com o valor da tag
};

// Atualize a função handleSearch para receber um valor opcional:
const handleSearch = async (searchQuery = query) => {
  setLoading(true);
  setError(null);

  try {
    const params = { palavra_resumo: searchQuery }; // Use o termo de busca
    const resultados = await buscarTrabalhos(params); // Realiza a busca
    onResults(resultados); // Exibe os resultados
  } catch (err) {
    setError('Erro ao buscar trabalhos. Tente novamente.');
  } finally {
    setLoading(false);
  }
};

export default SearchBar;
