import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import Footer from './Footer';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const HeaderWrapper = styled.header`
  width: 100%;
  padding: 10px 20px;
  background-color: #f8f9fa;
  box-shadow: 0 4px 2px -2px gray;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: sticky;
  top: 0;
  z-index: 1000;
`;

const Logo = styled.a`
  font-size: 24px;
  font-weight: bold;
  color: #333;
  text-decoration: none;

  &:hover {
    color: #007bff;
  }
`;

const SearchBarWrapper = styled.div`
  display: flex;
  gap: 10px;

  input {
    padding: 10px;
    font-size: 16px;
    border: 2px solid #ddd;
    border-radius: 24px;
  }

  button {
    padding: 10px 20px;
    background-color: #4285f4;
    color: white;
    border: none;
    border-radius: 24px;
    cursor: pointer;

    &:hover {
      background-color: #357ae8;
    }
  }
`;

const ResultsWrapper = styled.div`
  max-width: 800px;
  width: 100%;
  margin: 20px auto;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const ResultItem = styled.div`
  padding: 15px;
  border: 1px solid #ddd;
  border-radius: 10px;
  background-color: #f9f9f9;

  h3 {
    color: #1a73e8;
    margin: 0;
  }

  p {
    color: #555;
    margin: 10px 0;
  }
`;

const PaginationWrapper = styled.div`
  display: flex;
  justify-content: center;
  gap: 5px;
  margin-top: 20px;

  button {
    padding: 8px 12px;
    border: 1px solid #ddd;
    border-radius: 5px;
    background-color: ${({ active }) => (active ? '#4285f4' : '#fff')};
    color: ${({ active }) => (active ? '#fff' : '#000')};
    cursor: pointer;

    &:hover {
      background-color: #357ae8;
      color: #fff;
    }
  }
`;

function Home() {
  const navigate = useNavigate();
  const [results, setResults] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 5;
  const buttonsPerPage = 10;

  useEffect(() => {
    // Simular dados de uma API
    const fetchData = async () => {
      const data = await fetch('http://138.197.15.35:5000/buscar');
      const json = await data.json();
      setResults(json);
    };
    fetchData();
  }, []);

  const handleSearch = () => {
    const filteredResults = results.filter((item) =>
      item.titulo.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setResults(filteredResults);
    setCurrentPage(1);
  };

  const totalPages = Math.ceil(results.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentResults = results.slice(indexOfFirstItem, indexOfLastItem);

  const firstVisiblePage = Math.floor((currentPage - 1) / buttonsPerPage) * buttonsPerPage + 1;
  const lastVisiblePage = Math.min(firstVisiblePage + buttonsPerPage - 1, totalPages);

  return (
    <PageWrapper>
      {/* Header */}
      <HeaderWrapper>
        <Logo href="/">ODS</Logo>
        <SearchBarWrapper>
          <input
            type="text"
            placeholder="Digite sua busca..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button onClick={handleSearch}>Buscar</button>
        </SearchBarWrapper>
      </HeaderWrapper>

      {/* Resultados */}
      <ResultsWrapper>
        {currentResults.map((result) => (
          <ResultItem key={result.id}>
            <h3>{result.titulo}</h3>
            <p>{result.resumo.substring(0, 100)}...</p>
            <button onClick={() => navigate(`/detalhes/${result.id}`)}>Saiba mais</button>
          </ResultItem>
        ))}
      </ResultsWrapper>

      {/* PaginaÃ§Ã£o */}
      {results.length > itemsPerPage && (
        <PaginationWrapper>
          {currentPage > 1 && <button onClick={() => setCurrentPage(1)}>Â« Primeiro</button>}
          {firstVisiblePage > 1 && (
            <button onClick={() => setCurrentPage(firstVisiblePage - buttonsPerPage)}>â¹</button>
          )}
          {Array.from({ length: lastVisiblePage - firstVisiblePage + 1 }, (_, i) => i + firstVisiblePage).map(
            (page) => (
              <button
                key={page}
                active={page === currentPage}
                onClick={() => setCurrentPage(page)}
              >
                {page}
              </button>
            )
          )}
          {lastVisiblePage < totalPages && (
            <button onClick={() => setCurrentPage(lastVisiblePage + 1)}>âº</button>
          )}
          {currentPage < totalPages && <button onClick={() => setCurrentPage(totalPages)}>Ãltimo Â»</button>}
        </PaginationWrapper>
      )}

      {/* Footer */}
      <Footer />
    </PageWrapper>
  );
}

export default Home;
