import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const ResultsWrapper = styled.div`
  max-width: 800px;
  margin: 20px auto;
`;

const ResultItem = styled.div`
  padding: 15px;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-bottom: 20px;
  background-color: #f9f9f9;

  h3 {
    color: #1a73e8;
    margin: 0;
  }

  p {
    color: #555;
    margin: 10px 0;
  }
`;

const StyledButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;

  &:hover {
    background-color: #357ae8;
  }
`;

const PaginationWrapper = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

const PageButton = styled.button`
  margin: 0 5px;
  padding: 10px 15px;
  border: 1px solid #ddd;
  background-color: ${({ active }) => (active ? '#4285f4' : '#fff')};
  color: ${({ active }) => (active ? '#fff' : '#000')};
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #357ae8;
    color: #fff;
  }
`;

function SearchResults({ results }) {
  const navigate = useNavigate();
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 5;
  const totalPages = Math.ceil(results.length / itemsPerPage);

  const handleSaibaMais = (result) => {
    localStorage.setItem('selectedResult', JSON.stringify(result));
    navigate(`/detalhes/${result.id}`);
  };

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  const currentResults = results.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  return (
    <div>
      <ResultsWrapper>
        {currentResults.map((result) => (
          <ResultItem key={result.id}>
            <h3>{result.titulo}</h3>
            <p>{result.resumo.substring(0, 300)}...</p>
            <StyledButton onClick={() => handleSaibaMais(result)}>Saiba mais</StyledButton>
          </ResultItem>
        ))}
      </ResultsWrapper>

      {/* Paginação */}
      {results.length > itemsPerPage && (
        <PaginationWrapper>
          {[...Array(totalPages).keys()].map((number) => (
            <PageButton
              key={number + 1}
              active={currentPage === number + 1}
              onClick={() => paginate(number + 1)}
            >
              {number + 1}
            </PageButton>
          ))}
        </PaginationWrapper>
      )}
    </div>
  );
}

export default SearchResults;
