import React, { useState } from 'react';
import styled from 'styled-components';

// Centraliza a barra de busca no meio da tela
const SearchWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 50vh; // Centraliza verticalmente
  transition: height 0.5s ease; // Transição suave ao subir
`;

const SearchInput = styled.input`
  width: 400px;
  padding: 10px;
  font-size: 18px;
  border: 2px solid #ccc;
  border-radius: 5px;
  &:focus {
    border-color: #007bff;
  }
`;

const SearchBar = ({ query, onSearch }) => {
  const [isScrolled, setIsScrolled] = useState(false);

  const handleInputChange = (e) => {
    onSearch(e.target.value);  // Chama a função passada via props
    if (!isScrolled) {
      setIsScrolled(true);  // Controla a posição da barra de busca
    }
  };

  return (
    <SearchWrapper style={{ height: isScrolled ? '20vh' : '50vh' }}>
      <SearchInput
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Digite sua pesquisa..."
      />
    </SearchWrapper>
  );
};

export default SearchBar;
