import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { buscarTrabalhos, buscarODS } from "../api";

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin: 20px 0;
`;

const Tag = styled.span`
  background-color: ${({ color }) => color || "#f1f3f4"};
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState([]);
  const [tagColors, setTagColors] = useState([]);
  const [currentTagIndex, setCurrentTagIndex] = useState(0);

  // Define as cores fixas (trocando o bege por roxo)
  const fixedColors = ["#FF5733", "#4285F4", "#FFA500", "#34A853", "#800080"]; // Vermelho, Azul, Laranja, Verde, Roxo

  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS();
        const odsTags = [];
        resultados.forEach((item) => {
          if (item.palavras_chave) {
            odsTags.push(...item.palavras_chave);
          } else if (item.titulo) {
            odsTags.push(item.titulo);
          }
        });

        setTags([...new Set(odsTags)].slice(0, 10)); // Remove duplicatas e pega até 10
        const shuffledColors = [...fixedColors].sort(() => Math.random() - 0.5);
        setTagColors(shuffledColors);
      } catch (err) {
        console.error("Erro ao carregar dados iniciais:", err);
        setError("Erro ao carregar dados iniciais.");
      }
    };

    carregarODS();
  }, []);

  // Atualizar tags a cada 5 segundos
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTagIndex((prevIndex) => (prevIndex + 5) % tags.length);
      const shuffledColors = [...fixedColors].sort(() => Math.random() - 0.5);
      setTagColors(shuffledColors);
    }, 5000);

    return () => clearInterval(interval);
  }, [tags, fixedColors]);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = { palavra_resumo: query };
      const resultados = await buscarTrabalhos(params);
      onResults(resultados);
    } catch (err) {
      setError("Erro ao buscar trabalhos. Tente novamente.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? "Buscando..." : "Pesquisar"}
      </SearchButton>
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Exibe as tags carregadas da API */}
      <TagsWrapper>
        {tags.slice(currentTagIndex, currentTagIndex + 5).map((tag, index) => (
          <Tag
            key={index}
            color={tagColors[index % tagColors.length]}
            onClick={() => {
              setQuery(tag);
              handleSearch(tag);
            }}
          >
            {tag}
          </Tag>
        ))}
      </TagsWrapper>
    </SearchWrapper>
  );
}

export default SearchBar;
