import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// Estilos para o wrapper da busca e header
const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;  /* Adiciona espaço para que a barra não fique atrás do header */
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;
  margin-bottom: 20px;

  &:hover {
    background-color: #357ae8;
  }
`;

const ResultsWrapper = styled.div`
  width: 600px;
  margin-top: 20px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 10px;
`;

const ResultItem = styled.div`
  margin-bottom: 20px;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 15px;
`;

const Nome = styled.h2`
  color: #1a73e8;
  margin: 0;
`;

const Descricao = styled.p`
  font-size: 14px;
  color: #333;
`;

const Data = styled.span`
  font-size: 12px;
  color: #777;
`;

const Keyword = styled.span`
  display: inline-block;
  background-color: ${props => props.color || '#f1f3f4'};  /* Cor dinâmica para cada tag */
  padding: 5px 10px;
  border-radius: 15px;
  margin-right: 5px;
  margin-bottom: 5px;
  font-size: 12px;
  cursor: pointer;
  color: white;

  &:hover {
    opacity: 0.8;
  }
`;

const SuggestionsList = styled.ul`
  list-style-type: none;
  padding: 0;
  margin-top: -10px;
  background-color: #fff;
  width: 600px;
  border: 1px solid #ddd;
  border-radius: 8px;
  max-height: 200px;
  overflow-y: auto;
`;

const SuggestionItem = styled.li`
  padding: 10px;
  cursor: pointer;
  &:hover {
    background-color: #f1f1f1;
  }
`;

function SearchBar() {
  const [query, setQuery] = useState('');
  const [pioneers, setPioneers] = useState([]);
  const [filteredPioneers, setFilteredPioneers] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [randomKeywords, setRandomKeywords] = useState([]);

  const colors = ['#34a853', '#ea4335', '#fbbc05', '#4285f4', '#f39c12'];  // Lista de cores para as tags

  // Carregar dados do JSON local
  useEffect(() => {
    fetch('/data/computing_pioneers.json')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao carregar o arquivo JSON');
        }
        return response.json();
      })
      .then((data) => {
        setPioneers(data.pioneers);
        generateRandomKeywords(data.pioneers);  // Gera palavras-chave a partir do JSON
      })
      .catch((error) => {
        console.error('Error loading JSON:', error);
      });
  }, []);

  // Função para gerar palavras-chave aleatórias a partir dos pioneiros
  const generateRandomKeywords = (pioneers) => {
    if (pioneers.length > 0) {
      const keywords = pioneers.map(pioneer => pioneer.nome);  // Usa os nomes como keywords
      const shuffled = keywords.sort(() => 0.5 - Math.random());  // Randomiza
      setRandomKeywords(shuffled.slice(0, 5));  // Seleciona 5 palavras aleatórias
    }
  };

  // Função de busca manual ao clicar no botão de pesquisa ou pressionar Enter
  const handleSearch = () => {
    const filtered = pioneers.filter(pioneer =>
      pioneer.nome.toLowerCase().includes(query.toLowerCase()) ||
      pioneer.descricao.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredPioneers(filtered);
    setSuggestions([]);  // Limpa as sugestões após a busca
  };

  // Função para filtrar as sugestões conforme o usuário digita
  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    setQuery(inputValue);

    // Filtra as sugestões com base no valor digitado
    if (inputValue.length > 0) {
      const filteredSuggestions = pioneers.filter(pioneer =>
        pioneer.nome.toLowerCase().includes(inputValue.toLowerCase())
      );
      setSuggestions(filteredSuggestions.slice(0, 10));  // Exibe no máximo 10 sugestões
    } else {
      setSuggestions([]);
    }
  };

  // Função chamada ao pressionar Enter
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();  // Realiza a busca ao pressionar Enter
    }
  };

  // Função chamada ao clicar em uma sugestão
  const handleSuggestionClick = (suggestion) => {
    setQuery(suggestion.nome);
    handleSearch();
  };

  // Função chamada ao clicar em uma tag de palavra-chave
  const handleKeywordClick = (keyword) => {
    setQuery(keyword);  // Define a busca com a palavra-chave clicada
    handleSearch();
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown}  // Captura a tecla Enter
        placeholder="Digite sua pesquisa..."
      />
      <SearchButton onClick={handleSearch}>Pesquisar</SearchButton>

      {/* Exibe as tags dinâmicas de exemplo */}
      <div>
        {randomKeywords.map((keyword, index) => (
          <Keyword
            key={index}
            onClick={() => handleKeywordClick(keyword)}
            color={colors[index % colors.length]}  // Cor dinamicamente atribuída
          >
            {keyword}
          </Keyword>
        ))}
      </div>

      {/* Exibe sugestões dinamicamente */}
      {suggestions.length > 0 && (
        <SuggestionsList>
          {suggestions.map((suggestion, index) => (
            <SuggestionItem
              key={index}
              onClick={() => handleSuggestionClick(suggestion)}
            >
              {suggestion.nome}
            </SuggestionItem>
          ))}
        </SuggestionsList>
      )}

      {/* Exibe os resultados da busca */}
      {filteredPioneers.length > 0 && (
        <ResultsWrapper>
          {filteredPioneers.map((pioneer) => (
            <ResultItem key={pioneer.nome}>
              <Nome>{pioneer.nome}</Nome>
              <Descricao>{pioneer.descricao}</Descricao>
              <Data>{pioneer.data}</Data>
            </ResultItem>
          ))}
        </ResultsWrapper>
      )}
    </SearchWrapper>
  );
}

export default SearchBar;
