function SearchBar({ onResults, onSearchPerformed }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = { palavra_resumo: query };
      const resultados = await buscarTrabalhos(params);
      onResults(resultados); // Envia os resultados ao componente pai
      onSearchPerformed(true); // Notifica que a busca foi realizada
    } catch (err) {
      console.error('Erro ao buscar trabalhos:', err);
      setError('Erro ao buscar trabalhos. Tente novamente.');
      onResults([]); // Define resultados como vazio em caso de erro
      onSearchPerformed(true); // Notifica que a busca foi realizada, mesmo sem resultados
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca..."
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}
