import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';
import { buscarTrabalhos } from './api';
import styled from 'styled-components';

const AppWrapper = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

const MainContent = styled.main`
  flex: 1;
  padding-bottom: 80px;
`;

function App() {
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);

  // Função para buscar por ODS
  const handleODSSelect = async (ods) => {
    try {
      const resultados = await buscarTrabalhos({ ods }); // Faz a busca por ODS
      setResults(resultados); // Define os resultados
      setError(null); // Limpa qualquer erro anterior
    } catch (err) {
      setError('Erro ao buscar resultados para a ODS selecionada.');
      setResults([]); // Limpa os resultados
    }
  };

  return (
    <AppWrapper>
      <Header onODSSelect={handleODSSelect} />
      <MainContent>
        <SearchBar onResults={setResults} />
        {error ? (
          <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>
        ) : (
          <SearchResults results={results} />
        )}
      </MainContent>
      <Footer />
    </AppWrapper>
  );
}

export default App;
