import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin: 20px 0; /* Espaçamento entre as tags e o botão */
`;

const Tag = styled.span`
  background-color: ${({ color }) => color || '#f1f3f4'};
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

function SearchBar({ onResults, onSearchAttempted }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [tags, setTags] = useState([]);
  const [tagColors, setTagColors] = useState([]);

  const fixedColors = ['#34A853', '#4285F4', '#F4B400', '#EA4335', '#F5DEB3']; // Verde, Azul, Laranja, Vermelho, Bege

  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS();
        const odsTags = [...new Set(resultados.flatMap((item) => item.palavras_chave || [item.titulo]))].slice(0, 5);
        setTags(odsTags);

        // Define cores únicas para cada tag
        const shuffledColors = [...fixedColors].sort(() => Math.random() - 0.5);
        setTagColors(shuffledColors.slice(0, odsTags.length));
      } catch (err) {
        console.error('Erro ao carregar tags:', err);
      }
    };

    carregarODS();
  }, []);

  const handleSearch = async (searchQuery = query) => {
    setLoading(true);

    try {
      const params = { palavra_resumo: searchQuery };
      const resultados = await buscarTrabalhos(params);
      onResults(resultados);
      onSearchAttempted(); // Notifica que a busca foi tentada
    } catch (err) {
      console.error('Erro ao buscar trabalhos:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleTagClick = (tag) => {
    setQuery(tag);
    handleSearch(tag);
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={() => handleSearch(query)} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {tags.length > 0 && (
        <TagsWrapper>
          {tags.map((tag, index) => (
            <Tag key={index} onClick={() => handleTagClick(tag)} color={tagColors[index]}>
              {tag}
            </Tag>
          ))}
        </TagsWrapper>
      )}
    </SearchWrapper>
  );
}

export default SearchBar;
