import React, { useState } from 'react';
import styled from 'styled-components';

// Centraliza a barra de busca no meio da tela
const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;  // Usa a altura total da tela para centralizar
`;

const SearchInput = styled.input`
  width: 600px;  // Mantém o campo de busca largo
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
  outline: none;
  transition: border-color 0.3s;
  
  &:focus {
    border-color: #4285f4;  // Azul ao focar
  }
`;

const Keywords = styled.div`
  font-size: 14px;
  color: #777;
  text-align: center;
`;

const Keyword = styled.span`
  margin: 0 8px;
  color: ${props => props.color};
  cursor: pointer;
  
  &:hover {
    text-decoration: underline;
  }
`;

function SearchBar({ query, onSearch }) {
  const [isScrolled, setIsScrolled] = useState(false);

  // Função para normalizar a string: remove acentos e converte para minúsculas
  function normalizeSearch(text) {
    return text
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');  // Remove acentos
  }

  function handleInputChange(e) {
    const normalizedQuery = normalizeSearch(e.target.value);  // Normaliza o valor inserido
    onSearch(normalizedQuery);  // Envia a busca normalizada
    if (!isScrolled) {
      setIsScrolled(true);
    }
  }

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Digite sua pesquisa..."
      />
      <Keywords>
        Exemplos: 
        <Keyword color="#fbbc05">Tecnologia</Keyword>
        <Keyword color="#ea4335">Livros</Keyword>
        <Keyword color="#34a853">Notícias</Keyword>
        <Keyword color="#4285f4">Saúde</Keyword>
        <Keyword color="#fbbc05">Esportes</Keyword>
        <Keyword color="#ea4335">Música</Keyword>
      </Keywords>
    </SearchWrapper>
  );
}

export default SearchBar;
