import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin: 20px 0;
`;

const Tag = styled.span`
  background-color: ${({ color }) => color || '#f1f3f4'};
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

const FeedbackMessage = styled.p`
  color: red;
  margin-top: 20px;
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState([]); // Tags iniciais (exibidas rapidamente)
  const [filteredTags, setFilteredTags] = useState([]); // Tags com artigos
  const [tagColors, setTagColors] = useState([]);
  const [feedback, setFeedback] = useState('');

  const fixedColors = ['#FF5733', '#4285F4', '#FFA500', '#34A853', '#800080'];

  // Carregar tags iniciais rapidamente
  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS();
        const uniqueTags = [...new Set(resultados.flatMap((item) => item.palavras_chave || []))];
        setTags(uniqueTags.slice(0, 5)); // Exibe rapidamente as primeiras 5 tags
        setTagColors([...fixedColors].sort(() => Math.random() - 0.5));

        // Verifica tags com conteúdo sem bloquear a interface
        verificarTagsComConteudo(uniqueTags);
      } catch (err) {
        console.error('Erro ao carregar dados iniciais:', err);
        setError('Erro ao carregar dados iniciais.');
      }
    };

    carregarODS();
  }, []);

  // Função para verificar tags com conteúdo
  const verificarTagsComConteudo = async (uniqueTags) => {
    try {
      const trabalhos = await buscarTrabalhos({});
      const tagsComConteudo = uniqueTags.filter((tag) =>
        trabalhos.some((trabalho) => trabalho.palavras_chave.includes(tag))
      );
      setFilteredTags(tagsComConteudo.slice(0, 5)); // Atualiza as tags visíveis com as verificadas
    } catch (err) {
      console.error('Erro ao verificar tags:', err);
    }
  };

  // Rotação de tags
  useEffect(() => {
    if (filteredTags.length > 0) {
      const interval = setInterval(() => {
        const shuffledTags = [...filteredTags].sort(() => Math.random() - 0.5);
        setFilteredTags(shuffledTags.slice(0, 5));
        setTagColors([...fixedColors].sort(() => Math.random() - 0.5));
      }, 6000);

      return () => clearInterval(interval);
    }
  }, [filteredTags]);

  const handleSearch = async (searchQuery = query) => {
    setLoading(true);
    setError(null);
    setFeedback('');

    try {
      const params = { palavra_resumo: searchQuery };
      const resultados = await buscarTrabalhos(params);

      if (resultados.length === 0) {
        setFeedback('Nenhum resultado encontrado. Tente outros termos ou refine sua busca.');
      }
      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  const handleTagClick = (tag) => {
    setQuery(tag);
    handleSearch(tag);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={() => handleSearch(query)} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {feedback && <FeedbackMessage>{feedback}</FeedbackMessage>}

      <TagsWrapper>
        {(filteredTags.length > 0 ? filteredTags : tags).map((tag, index) => (
          <Tag
            key={index}
            onClick={() => handleTagClick(tag)}
            color={tagColors[index % tagColors.length]}
          >
            {tag}
          </Tag>
        ))}
      </TagsWrapper>
    </SearchWrapper>
  );
}

export default SearchBar;
