import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const FeedbackMessage = styled.p`
  color: red;
  margin-top: 20px;
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [feedback, setFeedback] = useState('');

  // Identifica o tipo de busca baseado no termo inicial
  const identificarTipoDeBusca = (query) => {
    const lowerQuery = query.toLowerCase();

    if (lowerQuery.startsWith('data:')) {
      // Extraindo a data após "data:"
      const data = lowerQuery.replace('data:', '').trim();
      return { tipo: 'data', valor: data };
    }

    if (lowerQuery.startsWith('ods:')) {
      // Extraindo o número ODS após "ODS:"
      const ods = lowerQuery.replace('ods:', '').trim();
      return { tipo: 'ods', valor: parseInt(ods, 10) };
    }

    // Caso seja um termo de busca padrão
    return { tipo: 'palavra-chave', valor: query };
  };

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    setFeedback('');

    const { tipo, valor } = identificarTipoDeBusca(query);

    try {
      let resultados = [];

      if (tipo === 'data') {
        const params = { data: valor };
        resultados = await buscarTrabalhos(params);
      } else if (tipo === 'ods') {
        const params = { ods: valor };
        resultados = await buscarTrabalhos(params);
      } else if (tipo === 'palavra-chave') {
        const termos = valor.split(' '); // Divide em múltiplos termos
        const buscas = await Promise.all(
          termos.map((termo) => buscarTrabalhos({ palavra_resumo: termo }))
        );
        // Junta os resultados de todas as buscas e remove duplicatas
        resultados = [...new Set(buscas.flat())];
      }

      if (resultados.length === 0) {
        setFeedback('Nenhum resultado encontrado. Tente outros termos ou refine sua busca.');
      }

      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Ex.: data:2021, ODS:1, dignidade previdência"
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <FeedbackMessage>{error}</FeedbackMessage>}
      {feedback && <FeedbackMessage>{feedback}</FeedbackMessage>}
    </SearchWrapper>
  );
}

export default SearchBar;
