const BASE_URL = 'http://138.197.15.35:5000';

export async function buscarTrabalhos(params) {
  const queryString = new URLSearchParams(params).toString(); // Converte os parâmetros em string de consulta
  const response = await fetch(`${BASE_URL}/buscar?${queryString}`);
  
  if (!response.ok) {
    throw new Error('Erro ao buscar trabalhos: ' + response.statusText);
  }

  return response.json();
}
