import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api'; // Importar função para carregar dados iniciais

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 20px;
`;

const Tag = styled.span`
  background-color: #f1f3f4;
  color: #333;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    background-color: #e0e0e0;
  }
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState([]); // Estado para armazenar as tags carregadas da API

  // Carregar dados iniciais ao montar o componente
  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS(); // Chama a API para buscar os ODS ou palavras-chave
        const odsTags = resultados.map((item) => item.titulo || item.ODS[0]); // Obtém os títulos ou números do ODS
        setTags(odsTags.slice(0, 5)); // Exibe no máximo 5 tags iniciais
      } catch (err) {
        console.error('Erro ao carregar dados iniciais:', err);
      }
    };

    carregarODS();
  }, []);

  // Função de busca
  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = {};

      if (query) {
        params.palavra_resumo = query; // Usa o input como filtro
      }

      const resultados = await buscarTrabalhos(params);
      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  // Função para definir o termo da tag clicada no input
  const handleTagClick = (tag) => {
    setQuery(tag); // Atualiza o input com a tag clicada
    handleSearch(); // Realiza a busca automaticamente
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Exibe as tags carregadas da API */}
      <TagsWrapper>
        {tags.map((tag, index) => (
          <Tag key={index} onClick={() => handleTagClick(tag)}>
            {tag}
          </Tag>
        ))}
      </TagsWrapper>
    </SearchWrapper>
  );
}

export default SearchBar;
