import React, { useState } from 'react';
import styled from 'styled-components';

const HeaderWrapper = styled.header`
  position: fixed;  /* Faz o header ficar fixo no topo */
  top: 0;
  left: 0;
  width: 100%;
  background-color: #f8f9fa;
  padding: 20px;
  box-shadow: 0 4px 2px -2px gray;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 1000; /* Garante que o header fique acima de outros elementos */
`;

const Logo = styled.div`
  font-size: 24px;
  font-weight: bold;
  color: #333;
`;

const Links = styled.nav`
  a {
    margin-left: 15px;
    text-decoration: none;
    color: #007bff;
    &:hover {
      text-decoration: underline;
    }
  }
`;
const SearchBar = ({ query, onSearch }) => {
  const [isScrolled, setIsScrolled] = useState(false);

  const handleInputChange = (e) => {
    onSearch(e.target.value); // Passa o valor digitado para o componente pai
    if (!isScrolled) {
      setIsScrolled(true);  // Controla a posição da barra de busca
    }
  };

  return (
    <SearchWrapper style={{ height: isScrolled ? '20vh' : '50vh' }}>
      <SearchInput
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Digite sua pesquisa..."
      />
    </SearchWrapper>
  );
};

export default SearchBar;
