const BASE_URL = 'http://138.197.15.35:5000';

// Função já existente para buscar trabalhos
export async function buscarTrabalhos(params) {
  const queryString = new URLSearchParams(params).toString();
  const response = await fetch(`${BASE_URL}/buscar?${queryString}`);

  if (!response.ok) {
    throw new Error(`Erro ao buscar trabalhos: ${response.statusText}`);
  }

  return response.json();
}

// Nova função para buscar dados iniciais da API (exemplo: ODS ou palavras-chave)
export async function buscarODS() {
  const response = await fetch(`${BASE_URL}/buscar?ods=1`); // Simula uma consulta por ODS ou palavras-chave
  
  if (!response.ok) {
    throw new Error(`Erro ao buscar dados iniciais: ${response.statusText}`);
  }

  return response.json();
}
