import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

// Estilos para os elementos da busca e resultado
const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding-top: 50px;
  padding-bottom: 50px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const ResultsWrapper = styled.div`
  width: 600px;
  margin-top: 20px;
  padding: 20px;
  border: 1px solid #ddd;
  border-radius: 10px;
`;

const ResultItem = styled.div`
  margin-bottom: 20px;
  border: 1px solid #ddd;
  border-radius: 5px;
  padding: 15px;
`;

const Nome = styled.h2`
  color: #1a73e8;
  margin: 0;
`;

const Descricao = styled.p`
  font-size: 14px;
  color: #333;
`;

const Data = styled.span`
  font-size: 12px;
  color: #777;
`;

const Keyword = styled.span`
  display: inline-block;
  background-color: #f1f3f4;
  padding: 5px 10px;
  border-radius: 15px;
  margin-right: 5px;
  margin-bottom: 5px;
  font-size: 12px;
  cursor: pointer;

  &:hover {
    background-color: #4285f4;
    color: white;
  }
`;

function SearchBar() {
  const [query, setQuery] = useState('');
  const [pioneers, setPioneers] = useState([]);
  const [filteredPioneers, setFilteredPioneers] = useState([]);

  useEffect(() => {
    // Carregar dados do JSON local
    fetch('/data/computing_pioneers.json')
      .then((response) => response.json())
      .then((data) => setPioneers(data.pioneers));
  }, []);

  // Filtra os resultados com base na busca
  useEffect(() => {
    if (query) {
      const filtered = pioneers.filter((pioneer) =>
        pioneer.nome.toLowerCase().includes(query.toLowerCase()) ||
        pioneer.descricao.toLowerCase().includes(query.toLowerCase())
      );
      setFilteredPioneers(filtered);
    } else {
      setFilteredPioneers([]);
    }
  }, [query, pioneers]);

  // Função para normalizar o texto e remover acentos
  const normalizeText = (text) => {
    return text
      .toLowerCase()
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');  // Remove acentos
  };

  // Função chamada ao clicar em uma tag de palavra-chave
  const handleKeywordClick = (keyword) => {
    setQuery(normalizeText(keyword));
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(normalizeText(e.target.value))}
        placeholder="Digite sua pesquisa..."
      />

      {/* Exibe as tags de exemplo */}
      <div>
        <Keyword onClick={() => handleKeywordClick('Tecnologia')}>Tecnologia</Keyword>
        <Keyword onClick={() => handleKeywordClick('Livros')}>Livros</Keyword>
        <Keyword onClick={() => handleKeywordClick('Notícias')}>Notícias</Keyword>
        <Keyword onClick={() => handleKeywordClick('Saúde')}>Saúde</Keyword>
        <Keyword onClick={() => handleKeywordClick('Esportes')}>Esportes</Keyword>
      </div>

      {/* Exibe os resultados da busca */}
      {filteredPioneers.length > 0 && (
        <ResultsWrapper>
          {filteredPioneers.map((pioneer) => (
            <ResultItem key={pioneer.nome}>
              <Nome>{pioneer.nome}</Nome>
              <Descricao>{pioneer.descricao}</Descricao>
              <Data>{pioneer.data}</Data>
            </ResultItem>
          ))}
        </ResultsWrapper>
      )}
    </SearchWrapper>
  );
}

export default SearchBar;
