import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

// Estilos
const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const FeedbackMessage = styled.p`
  color: red;
  margin-top: 20px;
`;

// Função para normalizar texto (remover acentos, transformar em minúsculas)
const normalizarTexto = (texto) =>
  texto
    .normalize('NFD') // Normaliza acentos
    .replace(/[\u0300-\u036f]/g, '') // Remove acentos
    .toLowerCase(); // Converte para minúsculas

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [feedback, setFeedback] = useState('');

  // Identifica o tipo de busca com base no prefixo da entrada
  const identificarTipoDeBusca = (query) => {
    const lowerQuery = normalizarTexto(query.trim());

    if (lowerQuery.startsWith('data:')) {
      const data = lowerQuery.replace('data:', '').trim();
      return { tipo: 'data', valor: data };
    }

    if (lowerQuery.startsWith('ods:')) {
      const ods = lowerQuery.replace('ods:', '').trim();
      return { tipo: 'ods', valor: parseInt(ods, 10) };
    }

    return { tipo: 'palavra-chave', valor: query };
  };

  // Processa a busca
  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    setFeedback('');

    const { tipo, valor } = identificarTipoDeBusca(query);

    try {
      let resultados = [];

      if (tipo === 'data') {
        // Trata a entrada para buscar por ano ou data completa
        const params =
          valor.length === 4
            ? { data: `${valor}-01-01` } // Apenas ano (ex.: "2021")
            : { data: valor }; // Data completa (ex.: "2021-10-18")
        resultados = await buscarTrabalhos(params);
      } else if (tipo === 'ods') {
        const params = { ods: valor };
        resultados = await buscarTrabalhos(params);
      } else if (tipo === 'palavra-chave') {
        const termos = normalizarTexto(valor).split(/\s+/); // Divide os termos por espaços
        const buscas = await Promise.all(
          termos.map((termo) =>
            buscarTrabalhos({ palavra_resumo: termo }).catch(() => [])
          )
        );
        resultados = [...new Set(buscas.flat())]; // Junta resultados e remove duplicatas
      }

      if (resultados.length === 0) {
        setFeedback('Nenhum resultado encontrado. Tente outros termos ou refine sua busca.');
      }

      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // Permite buscar ao pressionar Enter
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        onKeyDown={handleKeyDown}
        placeholder="Ex.: data:2021, ODS:1, dignidade previdência"
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <FeedbackMessage>{error}</FeedbackMessage>}
      {feedback && <FeedbackMessage>{feedback}</FeedbackMessage>}
    </SearchWrapper>
  );
}

export default SearchBar;
