function SearchBar({ query, onResults }) {
  const [inputValue, setInputValue] = useState(query || ''); // Usa o valor inicial da query
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (query) {
      setInputValue(query);
      handleSearch(query); // Dispara a busca ao receber nova query
    }
  }, [query]);

  const handleSearch = async (searchQuery = inputValue) => {
    setLoading(true);
    setError(null);

    try {
      const resultados = await buscarTrabalhos({ palavra_resumo: searchQuery });
      onResults(resultados);
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
        placeholder="Ex.: data:2021, ODS:1, dignidade previdência"
      />
      <SearchButton onClick={() => handleSearch()} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <FeedbackMessage>{error}</FeedbackMessage>}
    </SearchWrapper>
  );
}

export default SearchBar;
