import React, { useState } from 'react';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';

function App() {
  const [query, setQuery] = useState('');  // Estado da busca
  const [showResults, setShowResults] = useState(false); // Controle para exibir resultados

  function handleSearch(input) {
    setQuery(input);
    if (input.length > 0) {
      setShowResults(true); // Exibe sugestões se houver input
    } else {
      setShowResults(false); // Oculta se o input for vazio
    }
  }

  return (
    <div>
      <Header />
      <SearchBar query={query} onSearch={handleSearch} />
      {showResults && <SearchResults query={query} />}
    </div>
  );
}

export default App;
