import React from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const ResultsWrapper = styled.div`
  max-width: 800px;
  margin: 20px auto;
`;

const ResultItem = styled.div`
  padding: 15px;
  border: 1px solid #ddd;
  border-radius: 10px;
  margin-bottom: 20px;
  background-color: #f9f9f9;

  h3 {
    color: #1a73e8;
    margin: 0;
  }

  p {
    color: #555;
    margin: 10px 0;
  }
`;

const StyledButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;

  &:hover {
    background-color: #357ae8;
  }
`;

function SearchResults({ results }) {
  const navigate = useNavigate();

  const handleSaibaMais = (result) => {
    // Salva o item selecionado no localStorage (ou ajuste conforme necessário)
    localStorage.setItem('selectedResult', JSON.stringify(result));
    navigate(`/detalhes/${result.id}`);
  };

  return (
    <ResultsWrapper>
      {results.map((result) => (
        <ResultItem key={result.id}>
          <h3>{result.titulo}</h3>
          <p>{result.resumo.substring(0, 300)}...</p>
          <StyledButton onClick={() => handleSaibaMais(result)}>Saiba mais</StyledButton>
        </ResultItem>
      ))}
    </ResultsWrapper>
  );
}

export default SearchResults;
