import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos, buscarODS } from '../api';

const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin: 20px 0; /* Espaçamento entre as tags e o botão */
`;

const Tag = styled.span`
  background-color: ${({ color }) => color || '#f1f3f4'};
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [tags, setTags] = useState([]); // Estado para as tags carregadas da API
  const [tagColors, setTagColors] = useState([]); // Estado para as cores dinâmicas

  // Gerar cores dinâmicas ao carregar o componente
  useEffect(() => {
    const generateColors = () => {
      const colors = ['#34a853', '#4285f4', '#fbbc05', '#ea4335', '#f39c12'];
      return Array.from({ length: 5 }, () =>
        colors[Math.floor(Math.random() * colors.length)]
      );
    };

    setTagColors(generateColors());
  }, []);

  // Carregar dados iniciais (tags) ao montar o componente
  useEffect(() => {
    const carregarODS = async () => {
      try {
        const resultados = await buscarODS(); // Busca dados da API
        const odsTags = [];
        resultados.forEach((item) => {
          if (item.palavras_chave) {
            odsTags.push(...item.palavras_chave);
          } else if (item.titulo) {
            odsTags.push(item.titulo);
          }
        });

        setTags([...new Set(odsTags)].slice(0, 5)); // Remove duplicatas e exibe no máximo 5 tags
      } catch (err) {
        console.error('Erro ao carregar dados iniciais:', err);
        setError('Erro ao carregar dados iniciais.');
      }
    };

    carregarODS();
  }, []);

  // Função de busca
  const handleSearch = async (searchQuery = query) => {
    setLoading(true);
    setError(null);

    try {
      const params = { palavra_resumo: searchQuery }; // Usa o termo de busca
      const resultados = await buscarTrabalhos(params); // Realiza a busca
      onResults(resultados); // Envia os resultados ao componente pai
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  // Função para buscar ao clicar em uma tag
  const handleTagClick = (tag) => {
    setQuery(tag); // Define a tag como valor do campo de busca
    handleSearch(tag); // Realiza a busca automaticamente
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={() => handleSearch(query)} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Exibe as tags carregadas da API */}
      {tags.length > 0 ? (
        <TagsWrapper>
          {tags.map((tag, index) => (
            <Tag
              key={index}
              onClick={() => handleTagClick(tag)}
              color={tagColors[index % tagColors.length]} // Cores dinâmicas
            >
              {tag}
            </Tag>
          ))}
        </TagsWrapper>
      ) : (
        <p style={{ color: '#888', marginTop: '20px' }}>Carregando tags...</p>
      )}
    </SearchWrapper>
  );
}

export default SearchBar;
