const TagsWrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin: 20px 0; /* Adiciona espaçamento acima e abaixo das tags */
`;

const Tag = styled.span`
  background-color: ${({ color }) => color || '#f1f3f4'};
  color: #fff;
  padding: 8px 16px;
  border-radius: 20px;
  font-size: 14px;
  cursor: pointer;

  &:hover {
    opacity: 0.8;
  }
`;

// Adicione as cores dinâmicas às tags
const tagColors = ['#34a853', '#4285f4', '#fbbc05', '#ea4335', '#f39c12'];

// Dentro da renderização das tags:
<TagsWrapper>
  {tags.length > 0 &&
    tags.map((tag, index) => (
      <Tag
        key={index}
        onClick={() => handleTagClick(tag)}
        color={tagColors[index % tagColors.length]} // Cor dinâmica
      >
        {tag}
      </Tag>
    ))}
</TagsWrapper>;
