import React, { useState } from 'react';
import styled from 'styled-components';
import { buscarTrabalhos } from '../api'; // Importa a função da API

// Estilos (mantêm os mesmos que você já criou)
const SearchWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: 150px;
`;

const SearchInput = styled.input`
  width: 600px;
  padding: 15px;
  font-size: 18px;
  border: 2px solid #ddd;
  border-radius: 24px;
  margin-bottom: 20px;
`;

const SearchButton = styled.button`
  padding: 10px 20px;
  background-color: #4285f4;
  color: white;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: #357ae8;
  }
`;

function SearchBar({ onResults }) {
  const [query, setQuery] = useState(''); // Estado do input
  const [loading, setLoading] = useState(false); // Estado de carregamento
  const [error, setError] = useState(null); // Estado de erro

  // Função de busca
  const handleSearch = async () => {
    setLoading(true);
    setError(null);

    try {
      const params = {}; // Cria um objeto para armazenar os parâmetros

      if (query) {
        params.palavra_resumo = query; // Usa o input como filtro
      }

      const resultados = await buscarTrabalhos(params); // Chama a API
      onResults(resultados); // Envia os resultados para o componente pai
    } catch (err) {
      setError('Erro ao buscar trabalhos. Tente novamente.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <SearchWrapper>
      <SearchInput
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Digite sua busca (ex.: dignidade, previdência)..."
      />
      <SearchButton onClick={handleSearch} disabled={loading}>
        {loading ? 'Buscando...' : 'Pesquisar'}
      </SearchButton>
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </SearchWrapper>
  );
}

export default SearchBar;


